package firstjavasessions;

public class StateCovidSwitchCases {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String state="Maharashtra";
		switch (state) {
		case "UP":
			System.out.println("in UP state");
			
			break;
			
		case "DELHI":
			System.out.println("in DELHI state");
			
			break;
			
		case "KARNATAKA":
			System.out.println("in KARNATAKA state");
			
			break;
			
		case "Maharashtra":
			System.out.println("in Maharashtra state");
			
			break;
			
		case "MP":
			System.out.println("in MP state");
			
			break;
			
		case "GOA":
			System.out.println("in GOA state");
			
			break;
			
		case "Uttarakhand ":
			System.out.println("in Uttarakhand state");
			
			break;

		default:
			System.out.println("enter valid state");
			break;
	}

	}
}
